            });
          });
        });
        //修改資料
        $(document).ready(function () {
          $("#users").on("click", "a", function (e) {
            var contentId = $(this).attr("id");
            // alert("id: " + contentId);
            $("label").text("要修改的資料ID為: " + contentId);
            //
            $("#update > a").on("click", function () {
              let name = $("#update > input[name=name]").val();
              if (!contentId || !name) {
